// ===== CLASSES =====
class GameConsole {
    constructor(brand, model, storageCapacity, isOn = false, photo) {
        this.brand = brand;
        this.model = model;
        this.storageCapacity = storageCapacity;
        this.isOn = isOn;
        this.installedGames = [];
        this.photo = photo;
    }
    togglePower() {
        this.isOn = !this.isOn;
    }
    installGame(game) {
        if (this.isOn) {
            this.installedGames.push(game);
        } else {
            alert(`Please turn on ${this.model} before installing games.`);
        }
    }
    getGames() {
        return this.installedGames;
    }
}

class HandheldConsole extends GameConsole {
    constructor(brand, model, storageCapacity, batteryLife, isOn = false, photo) {
        super(brand, model, storageCapacity, isOn, photo);
        this.batteryLife = batteryLife;
    }
}

class Game {
    constructor(name, genre, size, photo) {
        this.name = name;
        this.genre = genre;
        this.size = size;
        this.characters = [];
        this.photo = photo;
    }
    addCharacter(character) {
        this.characters.push(character);
    }
}

class GameCharacter {
    constructor(name, gender, description, photo) {
        this.name = name;
        this.gender = gender;
        this.description = description;
        this.photo = photo;
    }
}

// ===== DATA CREATION =====
const nintendoSwitch = new HandheldConsole(
    "Nintendo", "Switch", "256GB", 8, true,
    "data:image/jpeg;base64,"
);

const ps5 = new GameConsole(
    "Sony", "PlayStation 5", "1TB", true,
    "data:image/jpeg;base64,"
);

// Placeholder games
const zelda = new Game("Zelda: Breath of the Wild", "Adventure", 15,
    "data:image/jpeg;base64,");
const mario = new Game("Super Mario Odyssey", "Platformer", 10,
    "https://upload.wikimedia.org/wikipedia/en/8/8d/Super_Mario_Odyssey.jpg");

const spiderman = new Game("Spider-Man 2", "Action", 50,
    "data:image/jpeg;base64,");
const horizon = new Game("Horizon Forbidden West", "Adventure", 60,
    "data:image/jpeg;base64,");

// Preinstalled
nintendoSwitch.installGame(zelda);
nintendoSwitch.installGame(mario);
ps5.installGame(spiderman);
ps5.installGame(horizon);

const consoles = [nintendoSwitch, ps5];

// ===== RENDERING =====
const app = document.getElementById('app');

function renderConsoles() {
    app.innerHTML = '';
    consoles.forEach(consoleObj => {
        const consoleDiv = document.createElement('div');
        consoleDiv.className = 'console';
        consoleDiv.innerHTML = `
  <div class="console-header">
    <img src="${consoleObj.photo}" alt="${consoleObj.model}">
    <div>
      <h2>${consoleObj.model} (${consoleObj.brand})</h2>
      <p>💾 ${consoleObj.storageCapacity}</p>
      <p>⚡ Power: ${consoleObj.isOn ? 'ON' : 'OFF'}</p>
      ${consoleObj.batteryLife ? `<p>🔋 Battery: ${consoleObj.batteryLife} hrs</p>` : ''}
      <button class="toggle-btn">${consoleObj.isOn ? 'Turn OFF' : 'Turn ON'}</button>
      <button class="add-game-btn">➕ Add Game</button>
    </div>
  </div>
  <hr>
  <div class="games"></div>
`;

        // Add event listeners
        const toggleBtn = consoleDiv.querySelector('.toggle-btn');
        toggleBtn.addEventListener('click', () => {
            consoleObj.togglePower();
            renderConsoles();
        });

        const addGameBtn = consoleDiv.querySelector('.add-game-btn');
        addGameBtn.addEventListener('click', () => {
            if (!consoleObj.isOn) return alert(`${consoleObj.model} is OFF!`);
            const name = prompt("Enter game name:");
            const genre = prompt("Enter game genre:");
            const size = prompt("Enter game size (GB):");
            const photo = prompt("Enter game image URL:");
            if (name && genre && size && photo) {
                const newGame = new Game(name, genre, size, photo);
                consoleObj.installGame(newGame);
                renderConsoles();
            }
        });

        const gamesDiv = consoleDiv.querySelector('.games');
        consoleObj.getGames().forEach(game => {
            const gameDiv = document.createElement('div');
            gameDiv.className = 'game';
            gameDiv.innerHTML = `
    <img src="${game.photo}" alt="${game.name}">
    <h4>${game.name}</h4>
    <p>${game.genre} (${game.size}GB)</p>
    <button class="add-char-btn">Add Character</button>
    <div class="characters">
      ${game.characters.map(c => `
        <div>
          <img src="${c.photo}" title="${c.name}">
          <p>${c.name}</p>
        </div>
      `).join('')}
    </div>
  `;

            const addCharBtn = gameDiv.querySelector('.add-char-btn');
            addCharBtn.addEventListener('click', () => {
                const cname = prompt("Character name:");
                const gender = prompt("Gender:");
                const desc = prompt("Description:");
                const cphoto = prompt("Character image URL:");
                if (cname && gender && desc && cphoto) {
                    const newChar = new GameCharacter(cname, gender, desc, cphoto);
                    game.addCharacter(newChar);
                    renderConsoles();
                }
            });

            gamesDiv.appendChild(gameDiv);
        });

        app.appendChild(consoleDiv);
    });
}

renderConsoles();
